mapper.select:
        select 
	    
	    from kh_shop_info
            1=1 
        	 and poi = #{poi}
        	 and link like concat('%', #{link}, '%')
        	 and shop_name like concat('%', #{shopName}, '%')
        	 and phone =#{phone}
        	 and province =#{province}
        	 and city=#{city}
        	 and adress like concat('%', #{adress}, '%')
        	 and keyword like concat('%', #{keyword}, '%')
        	 and classic like concat('%', #{classic}, '%')
        	 and os =#{os}
        	 order by  create_date desc
